settings.custom_css:
.mouse-icon {
  width: 30px;
  height: 50px;
  border: 2px solid #fff;
  border-radius: 20px;
  position: relative;
  display: flex;
  justify-content: center;
  align-items: center;
}

.mouse-wheel {
  width: 4px;
  height: 10px;
  background: #fff;
  border-radius: 2px;
  animation: wheelBlink 1.2s infinite ease-in-out;
}

@keyframes wheelBlink {
  0%, 100% {
    opacity: 1;
    transform: translateY(0);
  }
  50% {
    opacity: 0.2;
    transform: translateY(5px);
  }
}
